// Global flag to prevent StrictMode double execution
let hasLoadedOnce = false;

export default function LoadingScreen({ onLoadingComplete }) {
  const [loadingStatus, setLoadingStatus] = useState("Initializing...");
  const [error, setError] = useState(null);

  useEffect(() => {
    // Prevent double loading in StrictMode
    if (hasLoadedOnce) {
      return;
    }
    hasLoadedOnce = true;

    const loadResources = async () => {
      try {
        const tasks = [
          {
            name: "Loading Face-api.js models",
            task: loadFaceApiModels,
          },
          {
            name: "Loading negative face descriptors",
            task: () =>
              fetch("/negative_face_descriptors_bundled.json").then((r) =>
                r.json()
              ),
          },
          {
            name: "Loading PCA parameters",
            task: () => fetch("/pca_parameters.json").then((r) => r.json()),
          },
        ];

        const results = {};

        for (const { name, task } of tasks) {
          setLoadingStatus(name);
          try {
            results[name] = await task();
          } catch (err) {
            console.error(`Failed to load ${name}:`, err);
            throw new Error(`Failed to load ${name}`);
          }
        }

        // After all initial loading is complete, run face processing warm-up
        // Only proceed if face-api models were successfully loaded
        if (results["Loading Face-api.js models"]) {
          setLoadingStatus("Warming up face processing...");
          try {
            const warmupResult = await warmupFaceProcessing();

            results["Face processing warm-up"] = warmupResult;
            console.log("Face processing warm-up completed:", warmupResult);
          } catch (err) {
            console.error("Face processing warm-up failed:", err);
            // Don't fail the entire loading process if warm-up fails
            results["Face processing warm-up"] = {
              success: false,
              error: err.message,
            };
          }
        } else {
          console.warn("Skipping face processing warm-up - models not loaded");
          results["Face processing warm-up"] = {
            success: false,
            error: "Models not loaded",
          };
        }

        setLoadingStatus("Ready!");
        setTimeout(() => {
          onLoadingComplete?.(results);
        }, 500);
      } catch (err) {
        setError(err.message);
      }
    };

    loadResources();
  }, [onLoadingComplete]);

  if (error) {
    return (
      <div className="flex h-dvh items-center justify-center text-white">
        <div className="text-center">
          <img
            src={kyaLogo}
            alt="zPass with KYA"
            className="mx-auto mb-8 h-48 w-48"
          />
          <p className="mb-4 text-red-500">Error: {error}</p>
          <button
            onClick={() => window.location.reload()}
            className="rounded bg-[#298ff9] px-4 py-2 hover:bg-blue-700"
          >
            Retry
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="h-svh text-white">
      <div className="flex min-h-[calc(100vh-120px)] flex-col items-center justify-center px-6 md:min-h-[calc(100vh-80px)]">
        <div className="mx-auto max-w-md text-center">
          {/* zPass Logo */}
          <div className="mb-16">
            <img src={kyaLogo} alt="KYA Logo" className="mx-auto h-48 w-48" />
          </div>

          <div className="mb-8 w-[250px]">
          <div className="mb-6 flex items-center justify-center gap-2">
              <span className="text-lg font-medium text-white">Loading</span>
              <div className="flex justify-center space-x-1">
                <div
                  className="h-1.5 w-1.5 animate-bounce rounded-full bg-white"
                  style={{ animationDelay: "0ms" }}
                ></div>
                <div
                  className="h-1.5 w-1.5 animate-bounce rounded-full bg-white"
                  style={{ animationDelay: "150ms" }}
                ></div>
                <div
                  className="h-1.5 w-1.5 animate-bounce rounded-full bg-white"
                  style={{ animationDelay: "300ms" }}
                ></div>
              </div>
            </div>
            <p className="text-xs text-gray-400">{loadingStatus}</p>
          </div>
        </div>

        {/* Footer Info */}
        <div
          className="absolute bottom-8 left-1/2 h-[171px] w-[337px] -translate-x-1/2 px-6"
          style={{
            backgroundImage: `url("data:image/svg+xml;charset=utf-8,${encodeURIComponent(bottomInfo)}")`,
            backgroundRepeat: "no-repeat",
          }}
        ></div>
      </div>
    </div>
  );
}
